import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Card from "./Card";
import Brownie from "./Brownie";

const Single = ()=> {

    const [ single, setSingle ] = useState({})
    const params = useParams()

    const url = `http://localhost:3005/api/${params.path}/${params.id}`

    useEffect(()=> {
        axios.get(url)
            .then(res => { 
                setSingle(res.data)
            })

    }, [])

    console.log(single)

    // const singleCard = single.map(single => {

    //     return (
    //                 <Card
    //                     key={single.single_id}
    //                     id={single.single_id}
    //                     name={single.name}
    //                     description={single.description}
    //                     imgUrl={single.flavorImg}
    //                     // path={'/:path/:id'}
    //                 />
    //     )
    // })



    return (
        <main className="main" id="singleMain">
            <div className="container">
                <div className='row'>
                    <div className="col-6">
                        <h2 className="text-capitalize text-center">{params.path}: {single.name}</h2>
                        <img src={`/images/${single.flavorImg}`} alt={`image of ${single.name}`} className="img-fluid d-block mx-auto single-image" />
                    </div>
                    <div className="col-6">
                        <div className="card h-100">
                            <div className="card-body">
                                <table class="table">
                                    <thead>
                                        <tr>
                                            <th scope="col">#</th>
                                            <th scope="col">First</th>
                                            <th scope="col">Last</th>
                                            <th scope="col">Handle</th>
                                        </tr>
                                    </thead>
                                    <tbody class="table-group-divider">
                                        <tr>
                                            <th scope="row">1</th>
                                            <td>{params.pat}</td>
                                            <td>Otto</td>
                                            <td>@mdo</td>
                                        </tr>
                                        <tr>
                                            <th scope="row">2</th>
                                            <td>Jacob</td>
                                            <td>Thornton</td>
                                            <td>@fat</td>
                                        </tr>
                                        <tr>
                                            <th scope="row">3</th>
                                            <td colspan="2">Larry the Bird</td>
                                            <td>@twitter</td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </main>
    )
}

export default Single
