import { useState } from "react";

const Login = ( { users } )=> {

    const [ loginData, setLoginData ] = useState({
        username: '',
        password: ''
    })

    const handleChange = (event)=> {

        const { name, value } = event.target

        setLoginData(prevState => {
            return {
                ...prevState,
                [name]: value
            }
        })
    }

    const [ access, setAccess ] = useState(false)

    const [ user, setUser ] = useState('')

    const handleSubmit = (event)=> {
        event.preventDefault()
        for (let i = 0; i < users.length; i++) {
            if ((users[i].username === loginData.username) && (users[i].password === loginData.password)) {

                setAccess(prevState => !prevState)
                setUser(users[i].username)
                setLoginData({
                    username: '',
                    password: ''
                })
            }
        }
        console.log(access)
        return access
    }

    return (
            <div className="container">
                <div className="row">
                    <div className="col-md-5">
                        <h1 className="display-1 text-capitalize h1-text">
                            Login Form
                        </h1>
                        {access && <p className="access-text">Greetings { user }
                        { loginData.username }</p>}
                    </div>
                    <div className="col-md-7">
                        <form onSubmit={handleSubmit} className="login-form">
                            <p className="form-text text-capitalize">member? login</p>
                            <div className="mb-3">
                                <div className="row">
                                    <div className="col-auto">
                                        <label 
                                            htmlFor="logininUserename" 
                                            className="form-label form-text"
                                        >Username</label>
                                        <input 
                                            type="text" 
                                            id="logininUsername"
                                            name="username"
                                            value={loginData.username}
                                            onChange={handleChange}
                                            className="form-control"
                                        />
                                    </div>
                                    <div className="col-auto">
                                        <label 
                                            htmlFor="logininPassword" 
                                            className="form-label form-text"
                                        >Password</label>
                                        <input 
                                            type="password" 
                                            id="logininPassword"
                                            name="password"
                                            value={logininData.password}
                                            onChange={handleChange}
                                            className="form-control"
                                        />
                                    </div>
                                    <div className="col-auto">
                                        <button type="submit" className="btn btn-success login-btn">
                                            login
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
    )
}

export default Login
