    return (
        <main className="main" id="singleMain">
            <div className="container">
                <div className='row'>
                    <div className="col-6">
                        <h2>{single.name}</h2>
                        <img src={`/images/${single.flavorImg}`} alt={`image of ${single.name}`} className="img-fluid img-fluid d-block mx-auto" />
                    </div>
                    <div className="col-6">
                        {/* {singleCard} */}
                        
                    </div>
                </div>
            </div>
        </main>
    )
}

export default Single
